document.querySelectorAll('.carousel-thumb img').forEach(e=>{e.addEventListener('click',function(){const t=document.getElementById('galleryModal'),n=document.getElementById('galleryModalImg');n.src=this.getAttribute('data-full'),t.style.display='block'})}),document.getElementById('galleryClose').onclick=function(){document.getElementById('galleryModal').style.display='none',document.getElementById('galleryModalImg').src=''},document.getElementById('galleryModal').onclick=function(e){e.target===this&&(this.style.display='none',document.getElementById('galleryModalImg').src='')};
